mapper.select:
select count_reply
from style
where style_no = #{style_no}

mapper.update:
update style
set style_content = #{style_content}
where style_no = #{style_no}

update style
set count_reply = count_reply + #{amount}
where style_no = #{style_no}

update style
set count_like = count_like + #{amount}
where style_no = #{style_no}

mapper.select:
select count_like
from style
where style_no = #{style_no}
